import React, { Component } from 'react';
class App extends Component {

  handleChange = (event) => {
    var dat = event.target.value;
  };
  render() {    
    return (
        <div className="offset-1 col-10">
          <label>Select User</label>
          <br/>
          <select name="" id="selectUser" onChange={this.handleChange}>
          {this.state.data.map((item, i) => (
            <option value={i + 1} id={i + 1} key={i}>User Id -{i + 1}</option>
          ))}
          </select>
          <h4>Json Listing Data</h4>
          <table width="100%" className="table table-bordered">
            <thead>
              <tr>
                <th width="8%">Sr No.</th>
                <th width="32%">Name</th>
                <th width="60%">Description</th>
              </tr>
            </thead>
            <tbody>
              {this.state.data.map((item, i) => (
                <tr className="card-body" key={i}>
                  <td width="8%" className="card-title">{item.id}</td>
                  <td width="32%" className="card-title">{item.title}</td>
                  <td width="60%" className="card-subtitle mb-2 text-muted">{item.body}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

    );
  }



  state = {data: []}

  componentDidMount() {
    var dataNo = document.getElementById('selectUser').value == "" ? 1 : document.getElementById('selectUser').value;
    var url_link = 'https://jsonplaceholder.typicode.com/posts?userId='+dataNo;
    fetch(url_link).then(res => res.json()).then((data) => {
      this.setState({ data: data })
      console.log(this.state.data)
    })
    .catch(console.log)
  }



  render() {    
    return (

        <div className="offset-1 col-10">
          <label>Select User</label>
          <br/>
          <select name="" id="selectUser" onChange={this.handleChange}>
          {this.state.data.map((item, i) => (
            <option value={i + 1} id={i + 1} key={i}>User Id -{i + 1}</option>
          ))}
          </select>
          <h4>Json Listing Data</h4>
          <table width="100%" className="table table-bordered">
            <thead>
              <tr>
                <th width="8%">Sr No.</th>
                <th width="32%">Name</th>
                <th width="60%">Description</th>
              </tr>
            </thead>
            <tbody>
              {this.state.data.map((item, i) => (
                <tr className="card-body" key={i}>
                  <td width="8%" className="card-title">{item.id}</td>
                  <td width="32%" className="card-title">{item.title}</td>
                  <td width="60%" className="card-subtitle mb-2 text-muted">{item.body}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

    );
  }
}
export default App;
