import React, { Component } from 'react';
class App extends Component {
  state = {data: []}
  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/posts?userId='+1).then(res => res.json()).then((data) => {
      this.setState({ data: data })
      console.log(this.state.data)
    })
    .catch(console.log)
  }
  render() {    
    return (
      <div className="container">
        <div className="col-xs-12">
          <h1>Json Listing Data</h1>


          <table>
            <thead>
              <th>Data</th>
            </thead>
          {this.state.data.map((item, i) => (
            <tr index={index}>
              <td><span className="card-title">User - {item.userId}</span><span className="card-title">{item.id}. {item.title}</span><span className="card-subtitle mb-2 text-muted">{item.body}</span></td>
            </tr>
            ))}
          </table>


        </div>
      </div>
    );
  }
}
export default App;
