import React, { Component } from 'react';
class LoginForm extends Component {
  handleClick(event){
    if (this.refs.myInput !== null && this.refs.myInput !== null) {
      var input = this.refs.myInput.value;
    }
  }

  render() {    
    return (
      <section className="container-fluid">
        <div className="row justify-content-center">
          <div className="col-md-3 col-10 rounded border shadow p-3 mb-5 mt-5 bg-white " id="col-Login">
            <p className="text-center"><strong>Login Admin</strong></p>
              <div className="form-group d-none" id="errorLogin">User Name -> admin, password -> password</div>
              <div className="form-group">
                <label>Username</label>
                <input type="text" id="user" name="user" className="form-control" ref="myInput" placeholder="admin" />    
              </div>
              <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control"  id="password" name="password"  placeholder="password" required />
              </div>
              <div className="form-group">
                <button type="button" onClick={(event) => this.handleClick(event)} className="btn btn-primary float-right">Submit</button>
              </div>
            </div>
          </div>
      </section>
    );
  }
}
export default LoginForm;
