import React, { Component } from 'react';
class LoginForm extends Component {
  handleClick(event){
    console.log(this)
    console.log(this.name)
    if (this.refs.userId !== null && this.refs.password !== null) {
      var userId = this.refs.userId.value;
      var password = this.refs.password.value;
      if(userId === "admin" && password === "password"){
        document.getElementById('LoginForm').classList.add("d-none");
        document.getElementById('root').classList.remove("d-none");
        document.getElementById('errorLogin').classList.add("d-none");
      }
      else{
        document.getElementById('LoginForm').classList.remove("d-none");
        document.getElementById('root').classList.add("d-none");
        document.getElementById('errorLogin').classList.remove("d-none");
      }
    }
  }

  render() {    
    return (
      <section className="container-fluid">
        <div className="row justify-content-center">
          <div className="col-md-3 col-10 rounded border shadow p-3 mb-5 mt-5 bg-white " id="col-Login">
            <p className="text-center"><strong>Login Admin</strong></p>
              <div className="form-group">
                <label>Username</label>
                <input type="text" id="user" ref="userId" className="form-control" placeholder="admin" />    
              </div>
              <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control" id="password" ref="password"  placeholder="password" />
              </div>
              <div className="form-group text-danger d-none" id="errorLogin">UserId->1, password->password</div>
              <div className="form-group">
                <button type="button" onClick={(event) => this.handleClick(event)} className="btn btn-primary float-right">Submit</button>
              </div>
            </div>
          </div>
      </section>
    );
  }
}
export default LoginForm;
