import React, { Component } from 'react';
import $ from "jquery";
class App extends Component {
  handleChange = (event) => {
    this.componentDidMount();
  };  
  state = {data: []}
  componentDidMount() {
    var userIdNo = $('#selectUser').val() == null ? 1 : $('#selectUser').val()
    fetch('https://jsonplaceholder.typicode.com/posts?userId='+userIdNo).then(res => res.json()).then((data) => {
      this.setState({ data: data })
      console.log(this.state.data)
    })
    .catch(console.log)
  }  
  render() {    
    return (
        <div className="col-md-offset-1 col-md-10 col-12">
          <br/><br/>
          <h4>Select User</h4>
          <select name="" id="selectUser" onChange={this.handleChange}>
          {this.state.data.map((item, i) => (
            <option value={i + 1} id={i + 1} key={i}>User Id -{i + 1}</option>
          ))}
          </select>
          <br/><br/><br/><br/>
          <h5>Json Listing Data</h5>
          <table width="100%" className="table table-bordered">
            <thead>
              <tr>
                <th width="6%">Sr No.</th>
                <th width="32%">Name</th>
                <th width="60%">Description</th>
              </tr>
            </thead>
            <tbody>
              {this.state.data.map((item, i) => (
                <tr className="card-body" key={i}>
                  <td width="6%" className="card-title">{item.id}</td>
                  <td width="30%" className="card-title">{item.title}</td>
                  <td width="65%" className="card-subtitle mb-2 text-muted">{item.body}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>       
    );
  }
}
export default App;
