    .catch(function (error) {
    console.log(error);
    });
    }

  render() {    
    return (
      <section className="container-fluid">
        <div className="row justify-content-center">
          <div className="col-3 rounded border shadow p-3 mb-5 mt-5 bg-white " id="col-Login">
            <p className="text-center"><strong>Login Admin</strong></p>
              <div className="form-group d-none" id="errorLogin">User Name -> admin, password -> password</div>
              <div className="form-group">
                <label>Username</label>
                <input type="text" id="user" name="user" className="form-control" placeholder="Usuario" required />    
              </div>
              <div className="form-group">
                <label>Password</label>
                <input type="password" className="form-control"  id="password" name="password"  placeholder="Password" required />
              </div>
              <div className="form-group">
                <button type="button" onClick={(event) => this.handleClick(event)} className="btn btn-primary float-right">Submit</button>
              </div>
            </div>
            </div>
      </section>
    );
  }
}
export default LoginForm;
