import React, { Component } from 'react';
class App extends Component {
  state = {data: []}
  componentDidMount() {
    console.log(window.refs.userId)
    //var userId = this.refs.userId.value;
    fetch('https://jsonplaceholder.typicode.com/posts?userId='+userId).then(res => res.json()).then((data) => {
      this.setState({ data: data })
      console.log(this.state.data)
    })
    .catch(console.log)
  }
  render() {    
    return (
      <div className="container">
        <div className="col-xs-12">
          <h1>Json Listing Data</h1>
          {this.state.data.map((item, i) => (
          <div className="card" key={i}>
          <div className="card-body">
            <h5 className="card-title">User - {item.userId}</h5>
            <h5 className="card-title">{item.id}. {item.title}</h5>
            <h6 className="card-subtitle mb-2 text-muted">{item.body}</h6>
          </div>
        </div>
          ))}
        </div>
      </div>
    );
  }
}
export default App;
