import React, { Component } from 'react';
class App extends Component {
  state = {data: []}
  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/posts?userId='+1).then(res => res.json()).then((data) => {
      this.setState({ data: data })
      console.log(this.state.data)
    })
    .catch(console.log)
  }
  render() {    
    return (
      <div className="container">
        <div className="col-xs-12">
          <h1>Json Listing Data</h1>
          <table width="100%">
            <thead>
              <tr>
                <th width="5%">Sr No.</th>
                <th width="35%">Name</th>
                <th width="60%">Description</th>
              </tr>
            </thead>
            <tbody>
              {this.state.data.map((item, i) => (
                <tr className="card-body" key={i}>
                  <td width="7%" className="card-title">User - {item.userId}</td>
                  <td width="35%" className="card-title">{item.id}. {item.title}</td>
                  <td width="60%" className="card-subtitle mb-2 text-muted">{item.body}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
export default App;
